        );
        vk.IC = new Pairing.G1Point[](6);

        vk.IC[0] = Pairing.G1Point( 
            6808862908260841666087234177370763149935480476079600022896128990669323282542,
            11911449450573897993011788693198144913510354785680620317207226139322816882934
        );                                      

        vk.IC[1] = Pairing.G1Point( 
            14227923219770174576330297375473775203584240559203427332535626185141044389182,
            14003607166008059394104656293639002174805608617712900159810321468983383760576
        );                                      

        vk.IC[2] = Pairing.G1Point( 
            4966212753378531457907488851199846324256843776559903545235692820074765110513,
            13741397303735637906175719912461321513875247889958137044375980549277838451102
        );                                      

        vk.IC[3] = Pairing.G1Point( 
            11958795884288258655110128053035979408443927760149677280800531806569909035117,
            709923153033217865826608452914082562768575766025544493669933397465109801860
        );                                      

        vk.IC[4] = Pairing.G1Point( 
            17236321305527969402681952284040913305253346343792219930049977973685766365295,
            21001964179754988552104931806503292004742907975852807267451816368734946129860
        );                                      

        vk.IC[5] = Pairing.G1Point( 
            193994132775940350182813142086693450600247973530773557810592413656991376056,
            17127321652842445480640285405652343762257306764103341281122036813530834655455
        );                                      

    }
    function verify(uint[] memory input, Proof memory proof) internal view returns (uint) {
        uint256 snark_scalar_field = 21888242871839275222246405745257275088548364400416034343698204186575808495617;
        VerifyingKey memory vk = verifyingKey();
        require(input.length + 1 == vk.IC.length,"verifier-bad-input");
        // Compute the linear combination vk_x
        Pairing.G1Point memory vk_x = Pairing.G1Point(0, 0);
        for (uint i = 0; i < input.length; i++) {
            require(input[i] < snark_scalar_field,"verifier-gte-snark-scalar-field");
            vk_x = Pairing.addition(vk_x, Pairing.scalar_mul(vk.IC[i + 1], input[i]));
        }
        vk_x = Pairing.addition(vk_x, vk.IC[0]);
        if (!Pairing.pairingProd4(
            Pairing.negate(proof.A), proof.B,
            vk.alfa1, vk.beta2,
            vk_x, vk.gamma2,
            proof.C, vk.delta2
        )) return 1;
        return 0;
    }
    /// @return r  bool true if proof is valid
    function verifyProof(
            uint[2] memory a,
            uint[2][2] memory b,
            uint[2] memory c,
            uint[5] memory input
        ) public view returns (bool r) {
        Proof memory proof;
        proof.A = Pairing.G1Point(a[0], a[1]);
        proof.B = Pairing.G2Point([b[0][0], b[0][1]], [b[1][0], b[1][1]]);
        proof.C = Pairing.G1Point(c[0], c[1]);
        uint[] memory inputValues = new uint[](input.length);
        for(uint i = 0; i < input.length; i++){
            inputValues[i] = input[i];
        }
        if (verify(inputValues, proof) == 0) {
            return true;
        } else {
            return false;
        }
    }
}
